6.query:
timeseries {
  valuesOp1 = sum(dt.kubernetes.container.cpu_usage),
  valuesOp2 = sum(dt.kubernetes.node.cpu_allocatable)
}, filter: {
  k8s.cluster.name == $Cluster AND
  k8s.node.name == $Node
}, from: -2m
| fieldsAdd cpu_usage_percent = valuesOp1[] / valuesOp2[] * 100
| fieldsAdd cpu_usage_percent = arrayFirst(cpu_usage_percent)

