13.query:
timeseries {
  cpu_usage = sum(dt.kubernetes.container.cpu_usage, rollup:avg)
}, filter: {
  k8s.cluster.name == $Cluster AND
  k8s.node.name == $Node
}, by: { dt.entity.cloud_application_instance, k8s.pod.name }
| filter isNotNull(cpu_usage)
| sort cpu_usage desc
| limit 20

